virtualTable.sql:
select rodov.sigla, rodov.geom
from (
select st_buffer(st_transform(sede.geom, 4326)::geography, %bufferRadius%) as buffer
from (
select name, geom from sedes_municipais where name='%sedeName%') as sede) as bf
join rede_rodoviaria as rodov on st_covers(bf.buffer, st_transform(rodov.geom, 4326)::geography)
